/**
 * Set the localePage cookie based on the user consent in OneTrust
 * The localePage cookie is created ONLY if the user consent with Functional cookies (C0003)
 */
function setLocalePageCookie() {
    // On accepting Functional cookies.
    if (typeof OnetrustActiveGroups !== 'undefined' && OnetrustActiveGroups != null && OnetrustActiveGroups.includes("C0003")) {
        var locale = getCookie("localePage"),
        localePageAuthor = $("input#locale-page-author").val(),
        localePagePublish = $("input#locale-page-publish").val();
        if (localePageAuthor != null) {
            var authorCatLocale = localePageAuthor.match(/\/content\/[a-zA-Z]+\/[a-z\_A-Z]+/g),
            authorDegLocale = localePageAuthor.match(/\/content\/[a-z]{2}/g);
            if (authorCatLocale != null && authorCatLocale.length > 0) {
                locale = authorCatLocale.concat("html").join(".");
            } else if (authorDegLocale != null && authorDegLocale.length > 0) {
                locale = authorDegLocale.concat("html").join(".");
            }
        } else if (localePagePublish != null && localePagePublish.length > 0) {
            locale = (localePagePublish.charAt(0) == '/' ? "" : "/") + localePagePublish.concat(".html");
        }

        // Create localePage cookie
        if (locale != null) {
            setCookie('localePage', locale, 365)
        }
    }
}

/**
 *  Updates the cookies behavior if the user changed consent
 */
function updateCookiesConsent() {
  // Validating the localePage cookie
  if (!getCookie("localePage")) {
    setLocalePageCookie();
  }
}

/**
 * Initialize the logic needed for the OneTrust banner.
 */
function initOneTrustBanner() {
  setLocalePageCookie();

  // On accepting all OneTrust cookies.
  // Triggers GA, Eloqua and other multimedia logic if the user consent with ALL the OneTrust cookie categories.
  if (typeof OnetrustActiveGroups !== 'undefined' && OnetrustActiveGroups != null && OnetrustActiveGroups.includes("C0002") && OnetrustActiveGroups.includes("C0003") && OnetrustActiveGroups.includes("C0004")) {
    if (typeof showVideoGallery === 'function' || $('.technologyCookie').length) {
      showVideoGallery();
      $('.cookie-warning.PdpVideos').css("display", "none");
      $('.technologyCookie').css("display", "block");
    }

    if($('.textVideo_cookie').length){
      showTextVideo();
    }
    if ($('.cookie-content').length) {
      showCampaignVideo();
    }
    if($('.product-gallery').length){
      showVideoGalleryComponent();
    }

    if (typeof loadElqcfgJS == 'function') {
      loadElqcfgJS();
    }
    if ($('#eloquaForm').length) {
      createGAFields();
    }

    //text image video component
    if ($('.textImageVideo').length) {
      showTextImageVideo();
    }

    //Campaign Event Text video component
    if ($('.campaignEventTextVideo').length) {
      showCampaignEventTextVideo();
    }

    //Podcast component
    if ($('.podcastComponent_cookie').length) {
      showPodcastComponent();
      $('.cookie-warning.one-trust').css("display", "none");
      $('.podcastComponent_cookie').css("display", "block");
    }
  } else { // On rejecting ALL OneTrust cookies
    if ($('#eloquaForm').length) {
      createGAFields();
    }
	  /*podcast-oneTrustcookie on rejecting cookies*/
    $('.podcastComponent_cookie').css("display", "none");
    $('.cookie-warning.one-trust').css("display", "block");

     /*PDPTechnology,PDPMediaGallery,PDPMultimedia -oneTrustcookie on rejecting cookies*/
    $('.technologyCookie').css("display", "none");
    $('div.show-pdpmediagallery').css("display", "none");
    $('div.show-pdpMultimedia').css("display", "none");
    $('.cookie-warning.PdpVideos').css("display", "block");
  }

  // On accepting only Performance and Targeting OneTrust cookies.
  if (typeof OnetrustActiveGroups !== 'undefined' && OnetrustActiveGroups != null && OnetrustActiveGroups.includes("C0002") && OnetrustActiveGroups.includes("C0004")) {
      if (typeof showVideoGallery === 'function' || $('.galleryImageCookie').length) {
          showVideoGallery();
      }
  } else {
      $('.galleryImageCookie').css("display", "none");
      $('.cookie-warning.PdpMediaGallery').css("display", "block");
  }
  //On accepting only Performance and Targeting OneTrust cookies.
  if (typeof OnetrustActiveGroups !== 'undefined' && OnetrustActiveGroups != null && OnetrustActiveGroups.includes("C0002") && OnetrustActiveGroups.includes("C0004")) {
      if (typeof showVideoGallery === 'function' || $('.pdp-multimedia__video').length) {
          showVideoGallery();
      }
  } else {
      $('.show-pdpMultimedia').css("display", "none");
      $('.cookie-warning.PdpMultimedia').css("display", "block");
  }
}

/**
 * On 'document ready'
 */
$(document).ready(function() {
  // Interval to verify the OT SDK is available in the page and execute the business logic.
  let otSdkIntervalRuns = 0;

  let otSdkInterval = setInterval(function () {
    if ((typeof OnetrustActiveGroups !== 'undefined' && OnetrustActiveGroups != null && OnetrustActiveGroups !== '') || otSdkIntervalRuns === 30) {
      clearInterval(otSdkInterval);
      initOneTrustBanner();

      // Setting up the listener to open the OT banner on demand
      $('.cookie-settings').on('click', function(event) {
        event.preventDefault();
        OneTrust.ToggleInfoDisplay();
      });

      // Setting a listener to look for changes in OT banner and apply the changes in live time.
      window.addEventListener("OneTrustGroupsUpdated", function() {
        updateCookiesConsent();
        if (OnetrustActiveGroups.includes("C0001")) {
          initOneTrustBanner();
        }
      });
    }otSdkIntervalRuns+=1;
  }, 300);
});

