  function scrollParentToChild(parent, child) {
    var parentRect = parent.getBoundingClientRect();
    var parentViewableArea = {
      height: parent.clientHeight,
      width: parent.clientWidth
    };
    var childRect = child.getBoundingClientRect();
    var isViewable = (childRect.top >= parentRect.top) && (childRect.top + childRect.height <= parentRect.top + parentViewableArea.height);
    if (!isViewable) {
      $(parent).animate({
        scrollTop: (childRect.top + parent.scrollTop) - parentRect.top
      });
    }
    isViewable = (childRect.left >= parentRect.left) && (childRect.left + childRect.width <= parentRect.left + parentViewableArea.width);
    $(parent).animate({
      scrollLeft: (childRect.left + parent.scrollLeft) - parentRect.left - ($(parent).width()/2) + ($(child).width()/2)
    });
  }
  
