$(document).ready(function() {
	
  function dataLayerPushForList($listWrapper){
		   var products = [],
		    listName = "";
		   $listWrapper.find('.dl-push-products').each(function(i, element) {
		    	$liElement = $(element).parent();
		    if(!($liElement.hasClass('hidden') || $liElement.hasClass('list-dl-pushed')) ){	
		      products.push({
		        'position' : $(element).data('position'),
		        'name' :  $(element).data('name').toString() ,
		        'id' :typeof $(element).data('id') !== 'undefined' ? $(element).data('id').toString() : '',
		        'price' : $(element).data('price'),
		        'brand' : $(element).data('brand'),
		        'category' : $(element).data('category')
		      });
		      $liElement.addClass("list-dl-pushed");		      
		    }
		    });
		  
		  if ($listWrapper.hasClass("list--detailed"))
		  {
			  listName = "Auth Product Detail Card List";
	      }else if ($listWrapper.hasClass("list--simple-product")){
	    	  listName = "Auth Simple Product Card List";
	      }else{
	    	  listName= "Auth Product Text List";
	      }
							 
		    if(products.length > 0){  
		      dataLayer.push({
		        'event' : 'impressionReady',
		        'ecommerce' : {
		          'currencyCode' : 'USD',
		          'actionField' : {
		          'list' : listName
		        },
		          'impressions' : products
		        }
		      });
		    }
		
  }
  
  $('.list__list-col ul.list__items').on('change', function(){
	  $(this).find('.dl-push-products').each(function(i, element) {
		  $liElement = $(element).parent();
		  $liElement.removeClass("list-dl-pushed");		      
	  });
	  var $listWrapper = $(this);
	  setTimeout(function(){
		  dataLayerPushForList($listWrapper); 
	  }, 400);
  });
  
  
  var accordionClick = false;
  var tabsClick = false;
  $(".list.auth-track").each(function() {
	  var $list = $(this);
	  // If List component is embedded in Accordion
	  if($($list).closest('.accordion__item').length > 0){
		  setTimeout(function(){
			// If List component is embedded in Accordion and the accordion item is open on page load
			  if($($list).closest('.accordion__item').find('h2.accordion__heading').hasClass('open')){
				  dataLayerPushForList($list);
			  } 
			  // If the accordion item that contains the list component is clicked, then the datalayer push will be initiated
			  else if(!accordionClick) {
				  accordionClick = true;
				  var $accordion = $($list).closest('.accordions__group').find('h2.accordion__heading');
				  $($accordion).click(function(){
					  var $accordionContent = $(this).hasClass('open') && $(this).siblings('.accordion__body').find('.list.auth-track');
					  if($($accordionContent).length > 0){
						  dataLayerPushForList($accordionContent);
					  }			
				  })
			  }
		  }, 400);
	  } 
	  // If List component is embedded in Tabs
	  else if($($list).closest('.tabs__content-item').length > 0){
		  setTimeout(function(){
			  // If the first tab has the List component, then datalayer push is initiated
			  if($($list).closest('.tabs__content-item').closest('.slick-slide').length > 0 && $($list).closest('.slick-slide').hasClass('slick-active')){
				  dataLayerPushForList($list);
			  } 
			  // If the list component is in another tab, then the datalayer push will be initiated when the tab is viewed
			  else if(!tabsClick){
				  tabsClick = true;
				  var $tabs = $list.closest('.tabs.auth-track').find('.tabs__nav').find('.slick-slide');
				  $tabs.on('click', function(){
					  var index = $(this).data('slick-index');
					  var tabContent = $(this).closest('.tabs__nav').siblings('.tabs__content').find('div.slick-slide[data-slick-index="'+index+'"]');
					  if($(tabContent).find('.list.auth-track').length > 0){
						  dataLayerPushForList($(tabContent).find('.list.auth-track'));
					  }
				  });
			  }
		  }, 400);
	  } else {
		  setTimeout(function(){ 
			  dataLayerPushForList($list);
		  }, 400);
	  }
  });
  
  setTimeout(function(){ 
	  $(".load-more").on('click', function() {
		  var pt = $(this).parent();
			  dataLayerPushForList(pt); 
	  });
  }, 400);
  
  $(".dl-push-products").unbind('click').bind('click', function() {
    var el = $(this),
      listName="";
    if (el.parents().hasClass("list--detailed"))
    {
	  listName = "Auth Product Detail Card List";
    }else if (el.parents().hasClass("list--simple-product")){
  	  listName = "Auth Simple Product Card List";
    }else{
  	  listName= "Auth Product Text List";
    }
    dataLayer.push({
      'event' : 'productClick',
      'ecommerce' : {
        'currencyCode' : 'USD',
        'click' : {
          'actionField' : {
            'list' : listName
          },
          'products' : [ {
        	'position' : $(el).data('position'),
            'name' : $(el).data('name'),
            'id' : typeof $(el).data('id') !== 'undefined' ? $(el).data('id').toString() : '',
            'price' : $(el).data('price'),
            'brand' : $(el).data('brand'),
            'category' : $(el).data('category')
          } ]
        }
      }
    });
  });
});
