	function getDefaultUnitValueList(){
		var cookie = JSON.parse(getCookie('li_aemuinfo'));
		var defaultValue = 'us';
		if(cookie){
			var defaultTempSoM;
			var defaultMeasurementType;

			if(cookie.preferences){
				defaultTempSoM = cookie.preferences.tempSoM;
				defaultMeasurementType = cookie.preferences.systemOfMeasurement;
			}

			if(defaultTempSoM){
				defaultValue = defaultTempSoM === "usStandard" || defaultTempSoM === "imperial" ? 'us' : 'metric';
			}else if(defaultMeasurementType){
				defaultValue = defaultMeasurementType === "usStandard" || defaultMeasurementType === "imperial" ? 'us' : 'metric';
			}
		} else {
			defaultValue = window.defaultUnitType || window._cat_default_locale;
		}
		return defaultValue;
	}

