    function listLoadMore() {
        var lists = $('[data-list-id]');

        //Observer for changes in size when cards and images load and height is adjusted
        var resizeObserver = new ResizeObserver(function(entries) {
          for (var entry of entries){
            var parentSlickSlide = $(entry.target).closest(".slick-slide");
            if(parentSlickSlide.length && parentSlickSlide.hasClass("slick-active")){
                adjustHeight(entry.target);
            }
          }
        });

        for (var list of lists){
            if ($(list).closest('.tabs__content-item').length) {
                //If inside a tabs component, start observing for changes in size to adjust its height
                resizeObserver.observe(list);
            }
        }

        $('[data-list-id] .load-more .button').unbind();
        $('[data-list-id] .load-more .button').click(function() {
            groupID = $(this).parent().parent().attr('data-list-id');
            rowID = $(this).closest('[data-list-row]').attr('data-list-row');
            LMOffset = $('[data-list-id="'+ groupID +'"] .load-more').offset().top - 50;
            thisPage = $('[data-list-id="'+ groupID +'"] .list__items .list__item:not(.hidden)').length + 1;
            perPage = $('[data-list-id="'+ groupID +'"] input[data-list-per-page]').data('list-per-page') + thisPage - 1;
            pageCount = $('[data-list-id="'+ groupID +'"] .list__items .list__item').length;

            var currentList = lists.filter('[data-list-id="'+ groupID +'"]')[0];

            for ( i = thisPage; i <= perPage; i++ ) {
                $('[data-list-id="'+ groupID +'"] .list__items .list__item:nth-child(' + i + ')').removeClass('hidden');

                if ( i === pageCount ) {
                $('[data-list-id="'+ groupID +'"] .load-more').addClass('d-none');
                resizeObserver.unobserve(currentList);
				//If inside a tabs component, set overflow property value as unset to adjust its height
                $('[data-list-id="'+ groupID +'"]').closest('.tabs__content').find('.slick-list').css('overflow','unset');
            }
                // When load more is clicked, focus first invisible list item for Accessibility
                if ( i === thisPage ) {
                $('[data-list-id="' + groupID + '"] .list__items .list__item:nth-child(' + thisPage + ') .list__item-content').focus();
                }
            }
            thisPage = $('[data-list-id="'+ groupID +'"] .list__items .list__item:not(.hidden)').length;
            var itemAvailable = thisPage;

            if ( thisPage === pageCount ) { perPage = pageCount; }

            perPage = $('[data-list-id="'+ groupID +'"] input[data-list-per-page]').data('list-per-page');
            loadMoreCount = $('[data-list-row="'+ rowID +'"] .list__items .list__item.hidden').length;

            if ( loadMoreCount > perPage ) { loadMoreCount = perPage; }

            var itemMissing = pageCount - itemAvailable;

            if ( loadMoreCount > itemMissing) { loadMoreCount = itemMissing; }

            $('[data-list-row="'+ rowID +'"] .load-more .button .load-more__counter').text(Granite.I18n.get("load_more_text", [loadMoreCount]));
            $('[data-list-id="'+ groupID +'"] .pagination--current').html(thisPage);
            $('html,body').animate({scrollTop: LMOffset }, 1000);

            var contentHeight = $('.tabs__content .slick-list .slick-active > div').height();
            $('.tabs__content > .slick-list').height(contentHeight + 'px ');
        })
    }


