    function getFilterValue(items, group, selectedTopFilter, checkedFilters){
        let isCategoryToFilter = false;
        let isGroupMatching = false;

        items.each(function () {
            if(checkedFilters.includes($(this).text())){
                isCategoryToFilter = true;
            }
        });

        group.each(function () {
            if (isCategoryToFilter && selectedTopFilter === $(this).text()) {
            	isGroupMatching = true;
            }
        });

        return isCategoryToFilter && isGroupMatching;
    }

    function toggleFiltering(rowID) {
          checkedFilters = [];
          let selectedTopFilter;
          $('[data-list-row="' + rowID + '"] input:checked + label .filter__text').each(function (i) {
              checkedFilters[i] = $(this).text();
              selectedTopFilter = $(this).parent().parent().parent().attr('id');
          });
          $('[data-list-row="' + rowID + '"] .list__list-col .list__items .list__item').each(function() {
              moveItem = $(this).detach();
              moveItem.appendTo('[data-list-row="' + rowID + '"] .list__holder');
          });
          if ( checkedFilters.length ) {
              cardMatch = [];
              checkedFilters.sort();
              if ( checkedFilters.length === 1 ) {
                  for ( i = 0; i < checkedFilters.length; i++ ) {
                      moveItem = $('[data-list-row="' + rowID + '"] .list__holder .list__item .list__item__filters .filter__name:contains(' + checkedFilters[i] + ')').parent().parent().parent().parent().detach();
                      moveItem.each(function () {
                          let filterAux = $(this).find('.list__item__filters .filter__name');
                          let filterGroup = $(this).find('.list__item__filters .filter__group');
                          let isCorrect = getFilterValue(filterAux, filterGroup, selectedTopFilter, checkedFilters);
                          if(isCorrect){
                              $(this).appendTo('[data-list-row="' + rowID + '"] .list__list-col .list__items');
                          }else{
                              $(this).appendTo('[data-list-row="' + rowID + '"] .list__holder');
                          }
                      });
                  }
              } else {
                  $('[data-list-row="' + rowID + '"] .list__holder .list__item').each(function()  {
                      cardMatch = [];
                      $(this).find('.list__item__filters .filter__name').each(function(j) {
                          cardMatch[j] = $(this).text();
                      });
                      cardMatch.sort();
                      if ( arrayContainsAnotherArray(cardMatch, checkedFilters) ) {
                          moveItem = $(this).detach();
                          moveItem.appendTo('[data-list-row="' + rowID + '"] .list__list-col .list__items');
                      }
                  })
              }
          } else {
              $('[data-list-row="' + rowID + '"] .list__holder .list__item').each(function(i) {
                  moveItem = $(this).detach();
                  moveItem.appendTo('[data-list-row="' + rowID + '"] .list__list-col .list__items');
              });
              $('[data-list-row="' + rowID + '"] .filter .clear').removeClass('active');
          }
          sortListItems(rowID)
          refreshListPagination(rowID)
          alignRows(rowID)
          updateCounters(rowID)
          resetFilterURL()
      }

	function bindFilterObject() {
		$('.filter .clear').bind('click', function() {
			if ( $(this).hasClass('active') ) {
				$(this).parent().find('input:checked + label').trigger('click');
				checkedFilters = [];
			}
		});

		$('.filter .clear').on('keydown', function (event) {
			if ((event.type === "keydown" && event.keyCode === 13)) {
				if ( $(this).hasClass('active') ) {
					$(this).parent().find('input:checked + label').trigger('click');
					checkedFilters = [];
				}
			}
		});

		$('.filter .filter__group-filters__filter-item label').bind('click', function(event) {
			event.preventDefault();

			rowID = $(this).parent().parent().parent().parent().parent().parent().parent().parent().attr('data-list-row');

			if ( $(this).parent().find('input').is(':checked') ) {
				// unchecked
				if ( $('[data-list-row="' + rowID + '"] .clear').hasClass('active') ) {
					if ( ( $('[data-list-row="' + rowID + '"] input:checked').length - 1 ) < 1 ) {
						$('[data-list-row="' + rowID + '"] .clear').removeClass('active');
					}
				}
				$(this).parent().find('input').prop('checked', false);
			} else {
				// checked
				if ( !$('[data-list-row="' + rowID + '"] .clear').hasClass('active') ) {
					$('[data-list-row="' + rowID + '"] .clear').addClass('active');
				}
				$(this).parent().find('input').prop('checked', true);
				if ( !$('[data-list-row="' + rowID + '"] .filter .clear').hasClass('active') ) {
					$('[data-list-row="' + rowID + '"] .filter .clear').addClass('active');
				}
			}
			// filter magic here
			toggleFiltering(rowID)
		});

		$('.filter .filter__group-filters__filter-item').off().on('keyup', function (event) {
			if (event.keyCode === 13) {
				var exist = $(this).children('input[type=checkbox]:first-child').prop('checked');
				rowID = $(this).parent().parent().parent().parent().parent().parent().parent().attr('data-list-row');
				$(this).children('input[type=checkbox]:first-child').prop('checked', !exist);

				if (!exist) {
					$('[data-list-row="' + rowID + '"] .filter .clear').addClass('active');
				}
				if (!$(this).parent().find('input').is(':checked')) {
					$('[data-list-row="' + rowID + '"] .filter .clear').removeClass('active');
				}
				toggleFiltering(rowID);
			}
		})
	}	

