	function getPresetURL() {
		curPage = $(location).attr('href');

		if ( curPage.indexOf('?') > -1 ) {
			isLoadFilters = curPage.substr(curPage.indexOf('?') + 1);
			filterMatches = isLoadFilters.split('+');
			//presetListID = '#' + filterMatches[0];

			if ( filterMatches[0] === 'filter' ) { 
				for ( i = 1; i < filterMatches.length; i++ ) {
					filterNameFix = filterMatches[i];
					filterNameFix = filterNameFix.replaceAll('%20', ' ');
					filterNameGroup = $('.list.has-filters').find('.filter__group').find('.filter__text:contains("' + filterNameFix + '")');

					//$(presetListID).find('.filter__group').find('.filter__text:contains("' + filterMatches[i] + '")').parent().parent().find('label').trigger('click');
					filterNameGroup.parent().parent().find('label').addClass('preset_filter_click');
					filterNameGroup.parent().parent().parent().parent().find('.filter__group-title:not(.open)').trigger('click');
				}
				$('label.preset_filter_click').trigger('click');
				$('label.preset_filter_click').removeClass('preset_filter_click');
			}
		}
	}

	getPresetURL()

	function genFilterURL() {
		var stringURL = '?filter',
		    tempURL = '',
		    pageURL =  $(location).attr('href'),
		    removeDomain = '',
		    domainLength = 0;

		if (pageURL.indexOf("//") > -1) {
			removeDomain = pageURL.split('/')[2];
		} else {
			removeDomain = pageURL.split('/')[0];
		}

		domainLength = removeDomain.length;

		pageURL = pageURL.substr(pageURL.indexOf(removeDomain) + domainLength)

		if ( checkedFilters.length > 0 ) {
			for ( i = 0; i < checkedFilters.length; i++ ) {
				tempURL = '+' + checkedFilters[i];
				tempURL = tempURL.replaceAll(' ', '%20');
				stringURL += tempURL;
			}

			pageURL += stringURL;

			$('.url--panel .generate-url span').text(pageURL);
			$('.url--panel .generate-url').addClass('generated')

			if ( pageURL !== null && pageURL !== '' ) {
				navigator.clipboard.writeText(pageURL);
			}
		}
	}

	function bindGenURLClick() {
		if ( $('.url--panel .generate-url').length ) {
			$('.url--panel .generate-url').click(function(e) {
				e.preventDefault()
				genFilterURL()
			});
		}
	}

